import React from 'react'

const Lists = ({movieList,setMovieList}) => {
  return (
    <div class="flex font-sans">
    {movieList.map((movie) => (
        <div class="flex-none w-56 relative">
            <div key={movie.id}>
                <img src={`${movie.posterPath}`} alt="" class="absolute inset-0 w-full h-full object-cover rounded-lg" loading="lazy" />
            </div>

            <div class="flex flex-wrap">
                <h1 class="flex-auto font-medium text-slate-900">
                {movie.id}
                </h1>
                    <div class="w-full flex-none mt-2 order-1 text-3xl font-bold text-violet-600">
                        {movie.title}
                    </div>
                    <div class="text-sm font-medium text-slate-400">
                        {movie.average}
                    </div>
                </div>
                <p class="text-sm text-slate-500">
                    {movie.releaseDate}
                </p>
            </div>
          ))}
    </div>






  )
}
export default Lists;
