import React from 'react'

const Lists = ({movieList,setMovieList}) => {
  return (
    <div className='flex font-sans'>
    {movieList.map((movie) => (
        // <div className="bg-gray-100 flex items-center justify-between w-full px-2 py-1 mx-1 my-2 text-gray-600  border rounded">
        <div className="bg-gray-100 flex items-center justify-between w-full text-gray-600  border rounded" key={movie.id}>
            <div>
                <img src={`${movie.posterPath}`} alt="" class="absolute inset-0 w-full h-full object-cover rounded-lg" loading="lazy" />
            </div>

            <div className="flex flex-wrap">
                <h1 className="flex-auto font-medium text-slate-900">
                    {movie.id}
                </h1>
                <div className="w-full flex-none mt-2 order-1 text-3xl font-bold text-violet-600">
                    {movie.title}
                </div>
                <div className="text-sm font-medium text-slate-400">
                    {movie.average}
                </div>
            </div>
            <p className="text-sm text-slate-500">
                {movie.releaseDate}
            </p>
        </div>
    ))}
    <br></br>
    </div>

  )
}
export default Lists;
