import axios from 'axios';
import React, { useState } from 'react';
import './App.css';
import Lists from './components/Lists';

export default function App() {

  const [query, setQuery] = useState("");

  const [movieList,setMovieList]= useState([
    {
      id:'sample',
      title:'title1',
      posterPath:'/pIkRyD18kl4FhoCNQuWxWu5cBLM.jpg',
      releaseDate:'2022-04-22',
      average:'4.3'
    },
  ]);


  const option = {

    url:'https://api.themoviedb.org/3/movie/popular',
    method:'GET',
    params:{api_key:'84dcca61f9944a6e09e7dfcf27364b36'},
    responseType:'json'

  }

  let resultList = [];

  const search = () =>{

    axios(option)
    .then(function(response){
      let results = response.data.results;

      results.map((result)=>{
        let newList = {
          id: `${result.id}`,//유니크한 값을 표현하기 위해
          title: `${result.title}`,
          posterPath:`${result.poster_path}`,
          releaseDate:`${result.release_date}`,
          average:`${result.vote_average}`
        }
        console.log(newList);
        resultList.push(newList);
      })

      console.log(resultList);


    })
    .catch(function(){
      alert('Fail to Loading..');
    })
    .finally(function(){
      alert('Done!');
      console.log(movieList);

      console.log(resultList);
      setMovieList(resultList); 
      // movieList.map((movie)=>{
      //   console.log(movie.title);
      // })
    })
  }


  const queryHandler = (e) =>{
    setQuery(e.target.value);
  }  


  return (
    <div className="flex items-center justify-center w-screen h-screen bg-[#ddedc0]">
      <div className="w-full p-6 m-4 bg-white rounded shadow ">
    <nav className="py-4 px-6 text-sm font-medium">
      <ul className="flex space-x-3">
        <li>
          <p className="block px-3 py-2 rounded-md bg-sky-500 text-white">TOP RATED</p>
        </li>
        <li>
          <p className="block px-3 py-2 rounded-md bg-sky-500 text-white">TOP RATED</p>
        </li>
        <li>
          <p className="block px-3 py-2 rounded-md bg-sky-500 text-white">TOP RATED</p>
        </li>
      </ul>
    </nav>
    <ul className="divide-y divide-slate-100">
      <article className="flex items-start space-x-6 p-6">
        <img src="" alt="" width="60" height="88" className="flex-none rounded-md bg-slate-100" />
        <div className="min-w-0 relative flex-auto">
          <h2 className="font-semibold text-slate-900 truncate pr-20">{"movie.title"}</h2>
            <dl className="mt-2 flex flex-wrap text-sm leading-6 font-medium">
            <div className="absolute top-0 right-0 flex items-center space-x-1">
              <dt className="text-sky-500">
                <span className="sr-only">Star rating</span>
                <svg width="16" height="20" fill="currentColor">
                  <path d="M7.05 3.691c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.372 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.539 1.118l-2.8-2.034a1 1 0 00-1.176 0l-2.8 2.034c-.783.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.363-1.118L.98 9.483c-.784-.57-.381-1.81.587-1.81H5.03a1 1 0 00.95-.69L7.05 3.69z" />
                </svg>
              </dt>
              <dd>{"movie.starRating"}</dd>
            </div>
            <div>
              <dt className="sr-only">Rating</dt>
              <dd className="px-1.5 ring-1 ring-slate-200 rounded">{"movie.rating"}</dd>
            </div>
            <div className="ml-2">
              <dt className="sr-only">Year</dt>
              <dd>{"movie.year"}</dd>
            </div>
            <div>
              <dt className="sr-only">Genre</dt>
              <dd className="flex items-center">
                <svg width="2" height="2" fill="currentColor" className="mx-2 text-slate-300" aria-hidden="true">
                  <circle cx="1" cy="1" r="1" />
                </svg>
                {"movie.genre"}
              </dd>
            </div>
            <div>
              <dt className="sr-only">Runtime</dt>
              <dd className="flex items-center">
                <svg width="2" height="2" fill="currentColor" className="mx-2 text-slate-300" aria-hidden="true">
                  <circle cx="1" cy="1" r="1" />
                </svg>
                {""}
              </dd>
            </div>
            <div className="flex-none w-full mt-2 font-normal">
              <dt className="sr-only">Cast</dt>
              <dd className="text-slate-400">{"movie.cast"}</dd>
            </div>
            </dl>
        </div>
      </article>
    </ul>
    </div>
  </div>

    // <div className="flex items-center justify-center w-screen h-screen bg-[#ddedc0]">
    //   {/* <div className="w-full p-6 m-4 bg-white rounded shadow md:w-3/4 md:max-w-lg lg:w-3/4 lg: max-w-lg"> */}
