    //         <button className="h-10 px-4 rounded bg-[#6fcaed] text-white"
    //             onClick={search}>SEARCH
    //         </button>
    //     </div>

    //     {/* <Lists movieList={movieList}
    //              setMovieList={setMovieList}/> */}

    //   </div>


    // </div>
  );
}
