import axios from 'axios';
import { useState } from 'react';
import './App.css';

function App() {

  const [query, setQuery] = useState("");
  const [movieList, setMovieList] = useState([
    {
      id:'sample',
      title:'title1',
      posterPath:'/pIkRyD18kl4FhoCNQuWxWu5cBLM.jpg',
      releaseDate:'2022-04-22',
      average:'4.3'
    },
  ]);


  const option = {

    url:'https://api.themoviedb.org/3/movie/popular',
    method:'GET',
    params:{api_key:'84dcca61f9944a6e09e7dfcf27364b36'},
    responseType:'json'

  }

  const search = () =>{

    axios(option)
    .then(function(response){
      let results = response.data.results;

      results.map((result)=>{
          let movieList = {
            id: `${result.id}`,//유니크한 값을 표현하기 위해
            title: `${result.title}`,
            posterPath:`${result.poster_path}`,
            releaseDate:`${result.release_date}`,
            average:`${result.vote_average}`
          }
          setMovieList([...movieList,movieList]); 
          console.log(list);
      })
    })
    .catch(function(){
      alert('Fail to Loading..');
    })
    .finally(function(){
      alert('Done!');

      movieList.map((movie)=>{
        console.log(movie.title);
      })
    })
  }


  const queryHandler = (e) =>{
    setQuery(e.target.value);
  }  
  return (

    <div >
      <h1 class="text-3xl font-bold">BOX OFFICE LIST</h1>
      <div>
        <input type="text" value={query} onChange={queryHandler}/><button onClick={search}>SEARCH</button>
      </div>

      <div>
        <table>
          <thead>
            <tr>
              <th>id</th>
              <th>제목</th>
              <th>poster</th>
              <th>영화 개봉일</th>
              <th>평점</th>
            </tr>
          </thead>
          <tbody>
          {list.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.title}</td>
              <td><img src={`${movie.posterPath}`}/></td>
              <td>{movie.releaseDate}</td>
              <td>{movie.average}</td>
            </tr>
          ))}

          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
