import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd';

//구조 분해 할당을 이용
export default function Lists({todoData,setTodoData}) {


    const checkClick = (id) => {
        const updateTodo = todoData.map((data) => {
            if (data.id === id) {
                data.completed = !data.completed
            }
            return data;
        });

        setTodoData(updateTodo);
    }

    const deleteClick = (id) => {
        //해당 아이디에 대한 할일 목록을 지워야 해요
        if(window.confirm("Really Delete?")){
            const newTodoData = todoData.filter((data)=> data.id !== id)

            setTodoData(newTodoData);
        }
        else{
            return;
        }


    }

    const handleDrop=(e)=>{
        //만약에 drop end한 위치가 DragDropContext밖이면 return
        if(!e.destination) return;

        // copy todoData
        const updateTodo = todoData;

        //drag되는 요소 삭제
        const [reorder] = updateTodo.splice(e.source.index,1);

        //drop되는 위치에 삽입
        updateTodo.splice(e.destination.index,0,reorder);

        //순서 바꾼 배열로 setTodoData 
        setTodoData(updateTodo);

    }

    return (
        <div>
            <DragDropContext onDragEnd={handleDrop}>
                <Droppable droppableId='to-do'>
                    {(provided)=>(
                        <div {...provided.droppableProps} ref={provided.innerRef}>
                        {todoData.map((data,index) => (
                            <Draggable 
                                key={data.id}
                                draggableId={data.id.toString()}
                                index={index}>
                                    {(provided,snapshot)=>(
                                <div key={data.id}
                                {...provided.draggableProps} 
                                ref={provided.innerRef}
                                {...provided.dragHandleProps}>
                                    <div className={`${snapshot.isDragging?"bg-gray-300":"bg-gray-100"} 
                                                    flex items-center justify-between
                                                    w-full px-2 py-1 mx-1 my-2 text-gray-600  border rounded`}>
                                        <div className='items-center'>
                                            <input type="checkbox"
                                                    defaultChecked={false}
                                                    onChange={() => checkClick(data.id)} />
                                        {" "}<span className={data.completed ? "line-through" : undefined}>{data.title}</span>
                                        </div>
                                        <div className='items-center'>
                                            <button 
                                                className="h-10 px-4 rounded bg-[#fa6055] text-white"
                                                onClick={()=>deleteClick(data.id)}>Delete
                                                </button>
                                        </div>
                                    </div>
                                </div>
                                )}
                            </Draggable>
                        ))}
                        {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    )
}
