import React from 'react'

const List = React.memo(({id,title,completed,todoData,setTodoData,provided,snapshot,deleteClick}) => {

    console.log("List");

    const checkClick = (id) => {
        const updateTodo = todoData.map((data) => {
            if (data.id === id) {
                data.completed = !data.completed
            }
            return data;
        });

        setTodoData(updateTodo);
    }

    const editClick = (id,title) => {
        console.log(`${id}`)
        let span = document.getElementById(`${id}`);
        let btnEdit = document.getElementById(`btnEdit${id}`);
        let btnDelete = document.getElementById(`btnDelete${id}`);

        span.innerHTML = `<input onblur='spanReset(this)' type='text'className='w-full px-2 py-7 mr-4 text-gray-700 border rounded shadow'value=${title}>`;

        btnEdit.innerHTML = `<button>Save</button>`;
        btnDelete.innerHTML =  `<button className="h-10 px-4 rounded bg-[#ffd36e] text-white">Back</button>`
        document.getElementsByTagName('input')[0].focus();
    }



  return (
    <div key={id}
                                {...provided.draggableProps} 
                                ref={provided.innerRef}
                                {...provided.dragHandleProps}>
                                    <div className={`${snapshot.isDragging?"bg-gray-300":"bg-gray-100"} 
                                                    flex items-center justify-between
                                                    w-full px-2 py-1 mx-1 my-2 text-gray-600  border rounded`}>
                                        <div className='items-center' id={`title${id}`}>
                                            <input type="checkbox"
                                                    defaultChecked={false}
                                                    onChange={() => checkClick(id)} />
                                        {" "}<span id={id} className={completed ? "line-through" : undefined}>{title}</span>
                                        </div>
                                        <div className='items-center'>
                                            <button     
                                                className="h-10 px-4 rounded bg-[#ffd36e] text-white"
                                                onClick={()=>editClick(id,title)}
                                                id={`btnEdit${id}`}>Edit
                                            </button>
                                            <button 
                                                className="h-10 px-4 rounded bg-[#fa6055] text-white"
                                                onClick={()=>deleteClick(id)}
                                                id={`btnDelete${id}`}>Delete
                                            </button>
                                        </div>
                                    </div>
                                </div>
  )
});

export default List;
